// export default JI
class JI extends React.Component {
    state = {
      avocado: 0
    }
    render() {
      return(
        <div>
          <h1>{this.state.avocado}</h1>
          {this.state.avocado % 6 === 0 &&
            <button onClick={() => this.setState((mango) => ({avocado: ++mango.avocado}))}>
              CHART
            </button>}
          {this.state.avocado % 4 !== 0 &&
            <button onClick={() => this.setState((strawberry) => ({avocado: --strawberry.avocado}))}>
              EMPLOYEE
            </button>}
        </div>
      )
    }
  }
