import React, { useState } from 'react'

const ParagraphWidget = ({widget, editing, updateWidget, deleteWidget}) => {
    const [itemCache, setItemCache] = useState(widget)
    return(
        <>
            {
                editing &&
                <>
                    <textarea 
                    onChange={(e) => {
                        console.log(itemCache)
                        setItemCache({
                            ...itemCache,
                            text: e.target.value,
                        })
                    }}
                    value={widget.text} className="form-control"></textarea>
                </>
            }
            {
                !editing &&
                <p>
                    {widget.text}
                </p>
            }
        </>
    )
}

export default ParagraphWidget
