import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import EditableItem from '../editable-item'
import { useParams } from 'react-router-dom'
import topicService from '../../services/topic-service'

const TopicPills = ({
    title,
    deleteTopic,
    updateTopic,
    createTopic,
    findTopicsForLesson,
    topics = [],
    clearTopic,
}) => {
    const { layout, courseId, moduleId, lessonId, topicId } = useParams()
    useEffect(() => {
        if (lessonId !== 'undefined' && typeof lessonId !== 'undefined') {
            findTopicsForLesson(lessonId)
        }else{
            clearTopic()
        }
    }, [lessonId])

    return (
        <div className="row">
            <div className="col-11">
                <ul className="nav nav-pills">
                    {topics.map((topic) => (
                        <li className={`nav-item`} key={topic._id}>
                            <EditableItem
                                title={title}
                                to={`/courses/${layout}/editor/${courseId}/${moduleId}/${lessonId}/${topic._id}`}
                                item={topic}
                                active={topic._id === topicId}
                                deleteItem={deleteTopic}
                                updateItem={updateTopic}
                            />
                        </li>
                    ))}
                </ul>
            </div>
            <div className="col-1">
                <i
                    onClick={() => {
                        createTopic(lessonId)
                    }}
                    className="fas fa-plus"
                ></i>
            </div>
        </div>
    )
}

const stpm = (state) => {
    return {
        topics: state.topicReducer.topics,
    }
}
const dtpm = (dispatch) => {
    return {
        createTopic: (lessonId) => {
            topicService
                .createTopic(lessonId, { title: 'New Topic' })
                .then((theActualTopic) =>
                    dispatch({
                        type: 'CREATE_TOPIC',
                        topic: theActualTopic,
                    })
                )
        },
        deleteTopic: (item) => {
            topicService.deleteTopic(item._id).then((status) =>
                dispatch({
                    type: 'DELETE_TOPIC',
                    topicToDelete: item,
                })
            )
        },
        updateTopic: (topic) =>
            topicService.updateTopic(topic._id, topic).then((status) =>
                dispatch({
                    type: 'UPDATE_TOPIC',
                    topicToUpdate: topic,
                })
            ),
        findTopicsForLesson: (lessonId) =>
            topicService.findTopicsForLesson(lessonId).then((theTopics) =>
                dispatch({
                    type: 'FIND_TOPICS_FOR_LESSON',
                    topics: theTopics,
                })
            ),
        clearTopic: () =>
            dispatch({
                type: 'CLEAR_TOPIC',
                topics: [],
            }),
    }
}

export default connect(stpm, dtpm)(TopicPills)
