import React, { useState } from 'react'
import ParagraphWidget from './paragraph-widget'

const HeadingWidget = ({ item, updateWidget, deleteWidget, updateLocalWidget }) => {
    const [editing, setEditing] = useState(false)
    const [itemCache, setItemCache] = useState(item)
    const handleChange = (e) => {
        console.log(1)
        const new_type = {
            ...itemCache,
            type: e.target.value,
        }
        setItemCache(new_type)
        updateLocalWidget(new_type)
    }
    return (
        <>
            {!editing && (
                <>
                    {item.size === 1 && <h1>{item.text}</h1>}
                    {item.size === 2 && <h2>{item.text}</h2>}
                    {item.size === 3 && <h3>{item.text}</h3>}
                    {item.size === 4 && <h4>{item.text}</h4>}
                    {item.size === 5 && <h5>{item.text}</h5>}
                    {item.size === 6 && <h6>{item.text}</h6>}
                    <i
                        onClick={() => setEditing(true)}
                        className="fas fa-cog"
                    ></i>
                </>
            )}

            {editing && (
                <div>
                    <select
                        onChange={handleChange}
                        value={itemCache.type}
                        className="form-control"
                    >
                        <option value={'HEADING'}>Heading</option>
                        <option value={'PARAGRAPH'}>Paragraph</option>
                    </select>
                    <input
                        onChange={(e) => {
                            setItemCache({
                                ...itemCache,
                                text: e.target.value,
                            })
                        }}
                        value={itemCache.text}
                        className="form-control"
                    />
                    <select
                        onChange={(e) => {
                            setItemCache({
                                ...itemCache,
                                size: parseInt(e.target.value),
                            })
                        }}
                        value={itemCache.size}
                        className="form-control"
                    >
                        <option value={1}>Heading 1</option>
                        <option value={2}>Heading 2</option>
                        <option value={3}>Heading 3</option>
                        <option value={4}>Heading 4</option>
                        <option value={5}>Heading 5</option>
                        <option value={6}>Heading 6</option>
                    </select>
                    <i
                        onClick={() => {
                            setEditing(false)
                            updateWidget(itemCache)
                        }}
                        className="fas fa-check"
                    ></i>
                    <i
                        onClick={() => {
                            deleteWidget(item)
                            setEditing(false)
                        }}
                        className="fas fa-times"
                    ></i>
                </div>
            )}
        </>
    )
}

export default HeadingWidget
