import React from 'react'
export default class JI extends React.Component {
    state = {
        bm: 51
    }
    tn = () => this.state.bm = this.state.bm++
    wb = () => this.setState({
        bm: ++this.state.bm
    })
    te = () => this.setState(fo => ({
        bm: ++fo.bm
    }))
    render() {
        return(
            <div>
                <h1>{this.state.bm}</h1>
                <button onClick={this.tn}>QL</button>
                <button onClick={this.wb}>WD</button>
                <button onClick={this.te}>YF</button>
            </div>
        )
    }
}
