import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import EditableItem from '../editable-item'
import { useParams } from 'react-router-dom'
import lessonService from '../../services/lesson-service'

const LessonTabs = ({
    lessons = [],
    createLesson,
    updateLesson,
    deleteLesson,
    findLessonsForModule,
}) => {
    const { courseId, moduleId } = useParams()
    useEffect(() => {
        findLessonsForModule(moduleId)
    }, [])
    return (
        <>
            <ul className="nav nav-tabs">
                {lessons.map((lesson) => (
                    <li className="nav-item">
                        <EditableItem
                            to={`/courses/editor/${courseId}/${moduleId}/${lesson._id}`}
                            deleteItem={deleteLesson}
                            updateItem={updateLesson}
                            item={lesson}
                        />
                    </li>
                ))}
            </ul>
            <i
                onClick={() => createLesson(moduleId)}
                className="fas fa-plus fa-2x"
            ></i>
        </>
    )
}

const stpm = (state) => {
    return {
        lessons: state.lessonReducer.lessons,
    }
}
const dtpm = (dispatch) => {
    return {
        createLesson: (moduleId) => {
            moduleService
                .createLesson(moduleId, { title: 'New Lesson' })
                .then((theActualLesson) =>
                    dispatch({
                        type: 'CREATE_LESSON',
                        lesson: theActualLesson,
                    })
                )
        },
        deleteLesson: (item) => {
            lessonService.deleteLesson(item._id).then((status) =>
                dispatch({
                    type: 'DELETE_LESSON',
                    lessonToDelete: item,
                })
            )
        },
        updateLesson: (lesson) =>
            lessonService.updateLesson(lesson._id, lesson).then((status) =>
                dispatch({
                    type: 'UPDATE_LESSON',
                    module,
                })
            ),
        findLessonsForModule: (moduleId) =>
            lessonService.findLessonsForModule(moduleId).then((theLessons) =>
                dispatch({
                    type: 'FIND_LESSONS_FOR_MODULE',
                    modules: theLessons,
                })
            ),
    }
}

export default connect(stpm, dtpm)(LessonTabs)
