import React from 'react'
import CourseTable from "./course-table/course-table";
import CourseGrid from "./course-grid/course-grid";
import CourseEditor from "./course-editor/course-editor";

class CourseManager extends React.Component {
  state = {
    courses: [
      {title: "CS4321", owner: "frank", lastModified: "2/9/15"},
      {title: "CS5432", owner: "greg", lastModified: "3/8/25"},
      {title: "CS6543", owner: "herbert", lastModified: "4/7/35"},
      {title: "CS7654", owner: "ian", lastModified: "5/6/45"},
    ]
  }

  addCourse = () => {
    const newCourse = {
      title: "New Course",
      owner: "New Owner",
      lastModified: "Never"
    }
    this.state.courses.push(newCourse)
    this.setState(this.state)
  }

  deleteCourse = (courseToDelete) => {
    const newCourses = this.state.courses
      .filter(course => course !== courseToDelete)
    this.setState({
      courses: newCourses
    })
  }

  render() {
    return(
      <div className="headers">
        <div className="wbdv-sticky-top wbdv-padding-5px">
             <div className="row">
                 <div className="col-1 nav-btn">
                            <i className="fa fa-bars fa-2x"></i>
                        </div>
                        <div className="col-3 page-header d-none d-sm-block">
                            <h5>Course Manager</h5>
                        </div>
                        <div className="col-7">
                            <input className="form-control" placeholder="New Course Title"/>
                        </div>
                        <div className="col-1 add-new">
                            <i className="fa fa-plus fa-2x"></i>
                        </div>
                    </div>
                </div>
        <button onClick={this.addCourse}>Add Course</button>
        <CourseTable deleteCourse={this.deleteCourse} courses={this.state.courses}/>
        <CourseGrid deleteCourse={this.deleteCourse} courses={this.state.courses}/>
        <CourseEditor/>
      </div>
    )
  }
}

export default CourseManager
