import React, { useState } from 'react'

const ParagraphWidget = ({ widget, editing, updateWidget, deleteWidget }) => {
    const [itemCache, setItemCache] = useState(widget)
    return (
        <>
            {editing && (
                <>
                    <textarea
                        onChange={(e) => {
                            setItemCache({
                                ...itemCache,
                                text: e.target.value,
                            })
                        }}
                        value={widget.text}
                        className="form-control"
                    ></textarea>
                    <i
                        onClick={() => {
                            updateWidget(itemCache)
                        }}
                        className="fas fa-check"
                    ></i>
                    <i
                        onClick={() => {
                            deleteWidget(widget)
                        }}
                        className="fas fa-times"
                    ></i>
                </>
            )}
            {!editing && <p>{widget.text}</p>}
        </>
    )
}

export default ParagraphWidget
