import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import EditableItem from '../editable-item'
import { useParams } from 'react-router-dom'
import moduleService from '../../services/module-service'
import './module-list.css'

const ModuleList = ({
    myModules = [],
    title,
    createModule,
    deleteModule,
    updateModule,
    findModulesForCourse,
}) => {
    const { layout, courseId, moduleId } = useParams()
    useEffect(() => {
        findModulesForCourse(courseId)
    }, [])
    return (
        <div>
            <ul className="list-group">
                {myModules.map((module) => (
                    <li
                        className={`list-group-item ${
                            moduleId === module._id ? 'active' : ''
                        }`}
                        key={module._id}
                    >
                        <EditableItem
                            title={title}
                            to={`/courses/${layout}/editor/${courseId}/${module._id}`}
                            deleteItem={deleteModule}
                            updateItem={updateModule}
                            item={module}
                        />
                    </li>
                ))}
                <li className="list-group-item">
                    <i
                        onClick={() => createModule(courseId)}
                        className="fas fa-plus fa-2x"
                    ></i>
                </li>
            </ul>
        </div>
    )
}

const stpm = (state) => {
    return {
        myModules: state.moduleReducer.modules,
    }
}
const dtpm = (dispatch) => {
    return {
        createModule: (courseId) => {
            moduleService
                .createModule(courseId, { title: 'New Module' })
                .then((theActualModule) =>
                    dispatch({
                        type: 'CREATE_MODULE',
                        module: theActualModule,
                    })
                )
        },

        deleteModule: (item) => {
            moduleService.deleteModule(item._id).then((status) =>
                dispatch({
                    type: 'DELETE_MODULE',
                    moduleToDelete: item,
                })
            )
        },
        updateModule: (module) =>
            moduleService.updateModule(module._id, module).then((status) =>
                dispatch({
                    type: 'UPDATE_MODULE',
                    moduleToUpdate: module,
                })
            ),
        findModulesForCourse: (courseId) =>
            moduleService.findModulesForCourse(courseId).then((theModules) =>
                dispatch({
                    type: 'FIND_MODULES_FOR_COURSE',
                    modules: theModules,
                })
            ),
    }
}

export default connect(stpm, dtpm)(ModuleList)
